import { createGlobalStyle } from "styled-components";


export default createGlobalStyle`
    *{
        margin:0;
        padding: 0;
        box-sizing: border-box;
        outline: 0;
        color: white ;
    } 
    body{
        background-color:#000000;
    }
    img{
        width:100% ;
        height:200px ;
        border-radius:6px ;
    }

 ;
`
